import React, {useEffect, Fragment} from "react";
import {Redirect, Link} from "react-router-dom";
// import { connect } from "react-redux";
// import { createStructuredSelector } from "reselect";

// import { selectCartHidden } from "../../redux/cart/cart.selectors";
// import { selectCurrentUser } from "../../redux/user/user.selectors";
// import { signOutStart } from "../../redux/user/user.actions";
import {isAuthenticated, signout} from "../../apis";

import {ReactComponent as Logo} from "../../assets/crown.svg";

import { LogoContainer, OptionsContainer, OptionLink} from "./header.styles";

const Header = () => {
    const performRedirect = () => {
        return <Redirect to="/"/>;
    };
    const {user} = isAuthenticated();
    useEffect(() => {
        isAuthenticated();
    }, [user]);

    return (
        <div className="ui secondary menu" style={{position:"fixed",padding:"10px 10px 10px 10px"}}>
            <Link>
                <LogoContainer to="/">
                    <Logo className="logo"/>
                </LogoContainer>
            </Link>
            <div className="right menu" style={{right:"0px",position:"fixed"}}>
                <Link>
                    <Fragment>
                        <OptionsContainer> {
                            isAuthenticated() ? (
                                <OptionLink to="/#"
                                    onClick={
                                        () => {
                                            signout(() => {
                                                performRedirect();
                                            });
                                        }
                                }>
                                    Logout
                                </OptionLink>
                            ) : (
                                <OptionLink to="/signin">Login</OptionLink>
                            )
                        } </OptionsContainer>
                    </Fragment>
                </Link>
            </div>
        </div>
    )
}

