import React, {useEffect, Fragment} from "react";
import {Redirect, Link} from "react-router-dom";
// import { connect } from "react-redux";
// import { createStructuredSelector } from "reselect";

// import { selectCartHidden } from "../../redux/cart/cart.selectors";
// import { selectCurrentUser } from "../../redux/user/user.selectors";
// import { signOutStart } from "../../redux/user/user.actions";
import {isAuthenticated, signout} from "../../apis";

import {ReactComponent as Logo} from "../../assets/Vebed_Logo.svg";

import { LogoContainer, OptionsContainer, OptionLink} from "./header.styles";

const Header = () => {
    const performRedirect = () => {
        return <Redirect to="/"/>;
    };
    const {user} = isAuthenticated();
    useEffect(() => {
        isAuthenticated();
    }, [user]);

    return (
        <div
          class="ui secondary pointing menu"
          style={{
            backgroundColor: "#1b1c1d",
            overflow: "hidden",
            position: "fixed",
            top: 0,
            left: 0,
            paddingTop: "13px",
            width: "100%",
          }}
        >
          <Link>
            <LogoContainer to="/">
              <Logo className="logo" style={{ backgroundColor: "white" }} />
            </LogoContainer>
          </Link>
          <div class="right menu">
            <Link>
              <OptionsContainer>
                {isAuthenticated() ? (
                  <OptionLink
                    style={{ WebkitTextFillColor: "white" }}
                    to="/#"
                    onClick={() => {
                      signout(() => {
                        performRedirect();
                      });
                    }}
                  >
                    Logout
                  </OptionLink>
                ) : (
                  <OptionLink to="/signin">Login</OptionLink>
                )}{" "}
              </OptionsContainer>
            </Link>
          </div>
        </div>
      );
    };

// const mapStateToProps = createStructuredSelector({
